import mongoose from "mongoose";

const GeneratedPdfSchema = new mongoose.Schema({
    pdfUrl: {
        type: String,
        required: [true, "Pdf Url is required"],
        trim: true,
    },
    title: {
        type: String,
        trim: true,
    },
    creator: {
        type: mongoose.Schema.Types.ObjectId, ref: 'User',
        // required: [true, "Generated By is required"],
    },
    tags: {
        type: Array,
    },
    visibility: {
        type: Boolean,
        default: true,
    },
    contentInText: {
        type: String,
        default: false,
    }
}, {
    timestamps: true
});


export const PdfModel =  mongoose.model("GeneratedPdf", GeneratedPdfSchema);

